import { createSlice, configureStore } from '@reduxjs/toolkit';

//part 2
const todosSlice = createSlice({
    name: 'dodos',
    initialState: {
        AllTodos: [
            {
                id: 1,
                title: "Viec 1",
                completed: false,
            },
            {
                id: 3,
                title: "Viec 3",
                completed: false,
            },
        ]
    }
})

// reducer
const todosReducer = todosSlice.reducer

//store
const store = configureStore({
    reducer: {
        todosReducer, //22ms33ss
        // todos_Reducer: todosReducer, //21ms14ss
    }
});

// 19ms40ss
const todosSelector = state => state.todosReducer.AllTodos;

export { store, todosSelector };
