// part 4.2,
import { createSlice, nanoid } from '@reduxjs/toolkit';

const todosSlice = createSlice({
    name: 'todos',
    initialState: {
        allTodos: [
            {
                id: 1,
                title: "Viec 1",
                completed: true, //22ms12ss
            },
            {
                id: 2,
                title: "Viec 2",
                completed: false,
            },
            {
                id: 3,
                title: "Viec 3",
                completed: false,
            },
        ]
    },
    reducer: { //
        // 16ms57ss
        addTodo: {
            reducer(state, action) { // 17ms56ss
                state.allTodos.unshift(action.payload);
            },
            prepare(title) { //18ms43ss
                return { // -> action, 19ms53ss
                    payload: {
                        id: nanoid(),
                        title,
                        completed: false,
                    }
                }
            }
        }
    },
    // 25ms25ss
    markComplete(state, action) {
        state.all
    }
})

// reducer
const todosReducer = todosSlice.reducer

const todosSelector = state => state.todosReducer.allTodos;

// action
export const { addTodo } = todosSlice.actions;

export { todosReducer, todosSelector };
