// part 4.1, 15ms25ss
import { createSlice, nanoid } from '@reduxjs/toolkit';

const todosSlice = createSlice({
    name: 'todos',
    initialState: {
        allTodos: [
            {
                id: 1,
                title: "Viec 1",
                completed: false,
            },
            {
                id: 2,
                title: "Viec 2",
                completed: false,
            },
            {
                id: 3,
                title: "Viec 3",
                completed: false,
            },
        ]
    },
    // 7ms13ss
    reducers: {
        // 7ms53ss, 8ms58ss
        addTodo: (stateTai, actionTai) => {
            stateTai.allTodos.unshift({ //8ms58ss
                id: nanoid(), //15ms25ss
                // id: 4,
                title: actionTai.payload, //
                completed: false,
            })
        }
    }
})

// reducer
const todosReducer = todosSlice.reducer

const todosSelector = state => state.todosReducer.allTodos;

// action, 10ms29ss
export const { addTodo } = todosSlice.actions;

export { todosReducer, todosSelector };
