// 13ms23ss, 14ms52ss, 15ms31ss
import { createSlice, configureStore } from '@reduxjs/toolkit';

// 15ms49ss
const todosSlice = createSlice({
    name: 'dodos',
    initialState: {
        // 16ms21ss
        AllTodos: [
            {
                id: 1,
                title: "Viec 1",
                completed: false,
            },
            {
                id: 2,
                title: "Viec 2",
                completed: false,
            },
            {
                id: 3,
                title: "Viec 3",
                completed: false,
            },
        ]
    }
})

// reducer, 16ms46ss
const todosReducer = todosSlice.reducer

//store, 17ms17ss
const store = configureStore({
    reducer: {
        todosReducer
    }
});

// 19ms40ss
exxpo


// 18ms05ss
export default store;
