import { useDispatch, useSelector } from "react-redux";
import TodoForm from "./TodoForm";
import {
    todosSelector,
    markComplete, // 28ms03ss
    deleteId
} from "../store/reducer/todosSlice";


//part 4 
const Todos = () => {
    const todos = useSelector(todosSelector);
    const dispatch = useDispatch();

    // 23ms23ss
    const toggleTodoCompleted = todoId => {
        // console.log(todoId);
        // 28ms03ss
        dispatch(markComplete(todoId));
    }

    // 30ms58ss
    const deleteSingleTodo = todoId => {
        dispatch(deleteId(todoId));
    }

    return (
        <div className="todo-list">
            {/* 2ms17ss */}
            <TodoForm />
            <ul>
                {todos.map(todo => (
                    <li key={todo.id}
                        // 22ms12ss
                        className={todo.completed ? 'completed' : ''}>{todo.title}

                        {/* 20ms21ss */}
                        <input
                            type="checkbox"
                            checked={todo.completed}

                            //23ms23ss
                            onChange={toggleTodoCompleted.bind(this, todo.id)} />

                        {/* 30ms58ss */}
                        <button
                            onClick={deleteSingleTodo.bind(this, todo.id)}
                        >Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default Todos;
