            {
                id: 2,
                title: "Viec 2",
                completed: false,
            },
            {
                id: 3,
                title: "Viec 3",
                completed: false,
            },
        ]
    },
    reducers: { //v902xx1
        // 16ms57ss
        addTodo: {
            reducer(state, action) { // 17ms56ss
                state.allTodos.unshift(action.payload);
            },
