                return { // -> action, 19ms53ss
                    payload: {
                        id: nanoid(),
                        title,
                        completed: false,
                    }
                }
            }
        },
        // 25ms25ss
        markComplete(state, action) {
            const todoId = action.payload;
            state.allTodos = state.allTodos.map(todo => {
                if (todo.id === todoId) {
                    todo.completed = !todo.completed;
                }
                return todo;
            })
        },
