import { useDispatch, useSelector } from "react-redux";
import TodoForm from "./TodoForm";
import {
    todosSelector,
    markComplete,
    deleteTodo,
    getAllTodos
} from "../store/reducer/todosSlice";
import { useEffect } from "react";

//part 5 
const Todos = () => {
    const todos = useSelector(todosSelector);
    const dispatch = useDispatch();

    const toggleTodoCompleted = todoId => {
        dispatch(markComplete(todoId));
    }

    const deleteSingleTodo = todoId => {
        dispatch(deleteTodo(todoId));
    }

    // 1m42ss
    useEffect(() => {
        //send reuest to json placeholder -> dispatch, 
        //6ms17ss
        dispatch(getAllTodos('1'));

    }, [dispatch])

    return (
        <div className="todo-list">
            <TodoForm />
            <ul>
                {todos.map(todo => (
                    <li key={todo.id}
                        className={todo.completed ? 'completed' : ''}
                    >{todo.title}

                        <input
                            type="checkbox"
                            checked={todo.completed}
                            onChange={toggleTodoCompleted.bind(this, todo.id)} />

                        <button
                            onClick={deleteSingleTodo.bind(this, todo.id)}
                        >Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    )
}

export default Todos;
